/*Table structure for table `fun_user_information` */

DROP TABLE IF EXISTS `fun_user_information`;

CREATE TABLE `fun_user_information` (
  `id` int(10) unsigned NOT NULL COMMENT '用户id',
  `name` varchar(10) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL DEFAULT '' COMMENT '用户名称',
  `phone` varchar(11) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL DEFAULT '未填写' COMMENT '用户电话',
  `QQ` varchar(20) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL DEFAULT '未填写' COMMENT '用户QQ',
  `wechat` varchar(20) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT '未填写' COMMENT '用户微信',
  `card` varchar(20) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT '未填写' COMMENT '用户身份证',
  `unit` varchar(40) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT '未填写' COMMENT '用户单位',
  `privacy` int(1) DEFAULT '0' COMMENT '用户隐私，默认开启',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

